package myproject;

public class ArithmeticOperator {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int x=10,y=5,z=10;
        System.out.println(x+y);
        System.out.println(x-y);
        System.out.println(x*y);
        System.out.println(x/y);
        System.out.println(x%y);
        z++;
        System.out.println(z);
        x--;
        System.out.println(x);
    }

}
