package myproject;

public class BitWiseOperator {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int num1 = 50, num2 = 20;

        //Bitwise AND
        System.out.println("num1 & num2 = " + (num1 & num2));
        
        //Bitwise OR
        System.out.println("num1 | num2 = " + (num1 | num2) );
        
        //Bitwise XOR
        System.out.println("num1 ^ num2 = " + (num1 ^ num2) );
        
        //Binary Complement Operator
        System.out.println("~num1 = " + ~num1 );
        
       }
    }

