package myproject;

public class UnaryOperator {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int x=5,y=6,z=7;
        System.out.println("x : " + x++);
        System.out.println("x : "+ x);
        System.out.println("y : "+ y--);
        System.out.println("y : "+ y);
        System.out.println("z : "+ ++z);
        System.out.println("z : "+ --z);
    }

}
